import React, { useEffect, useState } from "react";
import searchImage from '../src/Images/search.png'
import cloud from '../src/Images/cloud.png'
import humid from '../src/Images/humidity.png'
import wind from '../src/Images/windy.png'
import axios from "axios";



const Weather =()=>{
    const[data,setData]=useState({
        celcius:10,
        name: 'London' ,
        humidity: 10,
        speed : 2
    });
    useEffect(()=>{
      const apiURL = 'https://api.openweathermap.org/data/2.5/weather?q=London&appid=16bfa98849718de13b6e8978b87d47b8&units=metric';
      axios.get(apiURL)
      .then(res=> {
        setData({...data, celcius: res.data.main.temp, name:res.data.name,
        humidity: res.data.main.humidity, speed: res.data.eind.speed})
      })
      .catch(err=> console.log(err))
    }, [])
     return( 
        <>
             <div className="container">
               <div className="weather"> 
                    <div className="search">
                    <input type="search" 
                          placeholder="Enter City Name" 
                          onChange={(event)=>{

                          } }
                          />      
                          <button> <img src={searchImage} alt="searchlogo"/></button>        
                     </div>

                 <div className="winfo">
                         <img src={cloud} alt="cloud" className="icon"/>
                         <h1>{data.celcius}</h1>
                         <h2>{data.name}</h2>
                          <div className="details">
                            <div className="col">
                                 <img src={humid} alt="humid"/>
                                  <div className="humidity">
                                    <p>{data.humidity}</p>
                                    <p>Humidity</p>
                                  </div>
                                  <div className="col">
                                 <img src={wind} alt="wind"/>
                                  <div className="wind">
                                    <p>{data.speed}</p>
                                    <p>wind</p>
                                  </div>
                            </div>
                         </div>

                     </div> 
               </div> 
               </div>
           </div> 
        </>
     );
};

export default Weather;
